mapper.select:
		select  count(*)
		from ( select agugunName
        from air_tb
        group by agugunName 
        having avg(adust) < ( select avg(a.adust)
                            from air_tb a join dongcode d
                            on a.agugunName = d.gugunName
                            where left(d.dongCode,5) = #{dongCode}
                            group by agugunName
                            ) 
               ) as t;
